                  role="separator"
                  className="
              ant-divider ant-divider-horizontal ant-divider-with-text-left
            "
                >
                  <span className="ant-divider-inner-text">
                    Coordinated Controls
                  </span>
                </div>
                <p>
                  Use <code>setFieldsValue</code> to set other controls value
                  programmaticly.
                </p>
                <div className="showcase">
                  <form className="ant-form ant-form-horizontal">
                    <div className="ant-row ant-form-item">
                      <div className="ant-col ant-col-5 ant-form-item-label">
                        <label
                          for="coordinated_note"
                          title="Note"
                          className="ant-form-item-required"
                        >
                          Note
                        </label>
                      </div>
                      <div className="ant-col ant-col-12 ant-form-item-control-wrapper">
                        <div className="ant-form-item-control">
                          <span className="ant-form-item-children">
                            <input
                              type="text"
                              data-__meta="[object Object]"
                              data-__field="[object Object]"
                              id="coordinated_note"
                              className="ant-input"
                            />
                          </span>
                        </div>
                      </div>
                    </div>
                    <div className="ant-row ant-form-item">
                      <div className="ant-col ant-col-5 ant-form-item-label"></div>
                      <div className="ant-col ant-col-12 ant-form-item-control-wrapper">
                        <div className="ant-form-item-control">
                          <span className="ant-form-item-children">
                            <Select
                              defaultValue="lucy"
                              style={{ marginLeft: "13%", width: "99%" }}
                              placeholder="Select Gender"
                            >
                              <Option value="lucy">Male</Option>
                              <Option value="jack">Female</Option>
                              <Option value="yiminghe">Other</Option>
                            </Select>
                          </span>
                        </div>
                      </div>
                    </div>
                    <div className="ant-row ant-form-item">
                      <div
                        className="
                    ant-col
                    ant-col-12
                    ant-col-offset-5
                    ant-form-item-control-wrapper
                  "
                      >
                        <div className="ant-form-item-control">
                          <span className="ant-form-item-children">
                            <button
                              type="submit"
                              className="ant-btn ant-btn-primary"
                            >
                              <span>Submit</span>
                            </button>
                          </span>
                        </div>
                      </div>
                    </div>
                  </form>
                </div>
                <section className="highlight-section">
                  <button
                    type="button"
                    className="btn-copy ant-btn ant-btn-primary ant-btn-sm"
                    onClick={(e) => {
                      navigator.clipboard.writeText(code);
                      setAlert(!alert);
                    }}
                  >
                    <i aria-label="icon: copy" className="anticon anticon-copy">
                      <svg
                        viewBox="64 64 896 896"
                        data-icon="copy"
                        width="1em"
                        height="1em"
                        fill="currentColor"
                        aria-hidden="true"
                        focusable="false"
                        className=""
                      >
                        <path d="M832 64H296c-4.4 0-8 3.6-8 8v56c0 4.4 3.6 8 8 8h496v688c0 4.4 3.6 8 8 8h56c4.4 0 8-3.6 8-8V96c0-17.7-14.3-32-32-32zM704 192H192c-17.7 0-32 14.3-32 32v530.7c0 8.5 3.4 16.6 9.4 22.6l173.3 173.3c2.2 2.2 4.7 4 7.4 5.5v1.9h4.2c3.5 1.3 7.2 2 11 2H704c17.7 0 32-14.3 32-32V224c0-17.7-14.3-32-32-32zM350 856.2L263.9 770H350v86.2zM664 888H414V746c0-22.1-17.9-40-40-40H232V264h432v624z"></path>
                      </svg>
                    </i>
                    <span>Copy</span>
                  </button>
                  {alert ? (
                    <Alert
                      message="Copied!"
                      type="success"
                      showIcon
                      action={<Button size="small" type="text"></Button>}
                      closable
                      className="alert-copy-code"
                    />
                  ) : (
                    ""
                  )}
                  <ReactMarkdown
                    children={markdown}
                    components={{
                      code({ node, inline, className, children, ...props }) {
                        const match = /language-(\w+)/.exec(className || "");
                        return !inline && match ? (
                          <SyntaxHighlighter
                            children={String(children).replace(/\n$/, "")}
                            language={match[1]}
                            PreTag="div"
                            {...props}
                            className="codetext"
                          />
                        ) : (
                          <code className={className} {...props}>
                            {children}
                          </code>
                        );
                      },
                    }}
                  />
                </section>
              </section>
              <section id="Dynamic-Rules" className="mb-24">
                <div
                  role="separator"
                  className="
              ant-divider ant-divider-horizontal ant-divider-with-text-left
            "
                >
                  <span className="ant-divider-inner-text">Dynamic Rules</span>
                </div>
                <p>
                  Perform different check rules according to different
                  situations.
                </p>
                <div className="showcase">
                  <Form
                    name="basic"
                    labelCol={{
                      span: 8,
                    }}
                    wrapperCol={{
                      span: 16,
                    }}
                    initialValues={{
                      remember: true,
                    }}
                    onFinish={onFinish}
                    onFinishFailed={onFinishFailed}
                    autoComplete="off"
                  >
                    <Form.Item
                      style={{ width: "60%" }}
                      label="Username"
                      name="username"
                      rules={[
                        {
                          required: true,
                          message: "Please input your username!",
                        },
                      ]}
                    >
                      <Input />
                    </Form.Item>

                    <Form.Item
                      style={{ width: "60%" }}
                      label="Password"
                      name="password"
                      rules={[
                        {
                          required: true,
                          message: "Please input your password!",
                        },
                      ]}
                    >
                      <Input.Password />
                    </Form.Item>

                    <Form.Item
                      name="remember"
                      valuePropName="checked"
                      wrapperCol={{
                        offset: 8,
                        span: 16,
                      }}
                    >
                      <Checkbox>Remember me</Checkbox>
                    </Form.Item>

                    <Form.Item
                      wrapperCol={{
                        offset: 8,
                        span: 16,
                      }}
                    >
                      <Button type="primary" htmlType="submit">
                        Submit
                      </Button>
                    </Form.Item>
                  </Form>
                </div>
